  // const [city, setCity] = useState("");
  // const [address, setAddress] = useState("");
  // const [pincode, setPincode] = useState("");
  // const [state, setState] = useState("");
  // const [orderId,setOrderID] = useState(Math.floor(Math.random()*10000));

  // useEffect(() => {
