  //     }
  // }).then(({ paymentIntent }) => {
  //     // paymentIntent = payment confirmation

  //     // db
  //     //   .collection('users')
  //     //   .doc(user?.uid)
  //     //   .collection('orders')
  //     //   .doc(paymentIntent.id)
  //     //   .set({
  //     //       basket: basket,
  //     //       amount: paymentIntent.amount,
  //     //       created: paymentIntent.created
  //     //   })

  //     // setSucceeded(true);
  //     // setError(null)
  //     // setProcessing(false)

  //     dispatch({
  //         type: 'EMPTY_BASKET'
  //     })

  //     history.replace('/orders')
  // })
  // };

  // const handleChange = (event) => {
  //   // Listen for changes in the CardElement
  //   // and display any errors as the customer types their card details
  //   setDisabled(event.empty);
  //   setError(event.error ? event.error.message : "");
  // };
  // const [medicineList, setMedicineList] = React.useState([]);

  const onOrder = (e) => {
    e.preventDefault();
    Axios.post("http://localhost:3001/order", {
      id: localStorage.getItem("order"),
      cid: localStorage.getItem("email"),
    }).then(console.log("YES"));

    basket.map((item) => {
      Axios.post("http://localhost:3001/basket", {
        mid: item.id,
        oid: localStorage.getItem("order"),
      }).then((response) => {
        console.log(response);
      });
    });
    // firestore.settings({
    //   timestampsInSnapshots: true,
    // });
    firestore
      .collection("orders")
      .add({
        oid: localStorage.getItem("order"),
        prescription: localStorage.getItem("prescription"),
        content: localStorage.getItem("pcontent"),
      })
      .then(() => {
        console.log("Vishal");
        history.push("/myorders");
      })
      .catch((e) => {
        console.log(e);
        alert(e.message);
      });
    console.log(basket);
  };

  return (
    <div className="payment">
      <div className="payment__container">
        <h1>
          Checkout (<Link to="/checkout">{basket?.length} items</Link>)
        </h1>

        {/* Payment section - delivery address */}
        <div className="payment__section">
          <div className="payment__title">
            <h3>Delivery Address</h3>
          </div>
          <div className="payment__address">
            {/* <p>{user?.email}</p> */}

            <p>{localStorage.getItem("address")}</p>
            <p>{localStorage.getItem("place")}</p>
            <p>{localStorage.getItem("state")}</p>
            <p>{localStorage.getItem("pincode")}</p>
          </div>
        </div>

        {/* Payment section - Review Items */}
        <div className="payment__section">
          <div className="payment__title">
            <h3>Review items and delivery</h3>
          </div>
          <div className="payment__items">
            {basket.map((item) => (
              <CheckoutProduct
                id={item.id}
                title={item.title}
                image={item.image}
                price={item.price}
                rating={item.rating}
              />
            ))}
          </div>
        </div>

        {/* Payment section - Payment method */}
        <div className="payment__section">
          <div className="payment__title">
            <h3>Payment Method</h3>
          </div>
          <div className="payment__details">
            {/* Stripe magic will go */}

            <form onSubmit={onOrder}>
              {/* <CardElement onChange={handleChange}/> */}

              <div className="payment__priceContainer">
                <CurrencyFormat
                  renderText={(value) => <h3>Order Total: {value}</h3>}
                  decimalScale={2}
                  value={getBasketTotal(basket)}
                  displayType={"text"}
                  thousandSeparator={true}
                  prefix={"$"}
                />
                <button type="submit">Place Order</button>
              </div>

              {/* Errors */}
              {/* {error && <div>{error}</div>} */}
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Payment;
